#include <stdio.h>#include <iostream>using namespace std;int main(){	int n,m,k=0;	int a[100],b[100];	cin >> n >> m;	for (int jn=0; jn<n; jn++)		cin >> a[jn];	for (int jm=0; jm<m; jm++)		cin >> b[jm];	for (int in=0; in<n; in++){		for (int im=0; im<m; im++){			if (a[in]==b[im]){			k = k + 1;				}		}		}// for (int kn=0; kn<n; kn++){// cout << a[kn] << " ";// }// cout<<endl;// for (int km=0; km<m; km++){// cout << b[km] << " ";// }// cout << endl;cout << k << endl; }